import { Router } from '@angular/router';
import { ObraService } from './../../../servicios/obra/obra.service';
import { Obra } from './../../../modelo/Obra';
import { Component } from '@angular/core';

@Component({
  selector: 'app-obra-list',
  templateUrl: './obra-list.component.html',
  styleUrls: ['./obra-list.component.css']
})
export class ObraListComponent {

  obra: Obra = new Obra();
  obras!: Obra[];

  constructor(private obraService: ObraService, private router: Router) { }

  ngOnInit() {
    this.cargarDatos();
  }

  cargarDatos() {
    let author: string | any = localStorage.getItem("id_Author");
    this.obraService.getByAuthor(author).subscribe(datos => {
      this.obras = datos;
    });
  }

}
